class Camisa:
    def __init__(self):
        self.__tamanho: str = ""

    def getTamanho(self) -> str:
        return self.__tamanho

    def SetTamanho(self, valor: str):
        if valor != "pp" or valor != "p" or valor != "m" or valor != "g" or valor != "gg" or valor != "xg":
            print("Tamanho inválido")
            return
        print(self.__tamanho)


def main():
    camisa = Camisa()

    while camisa.getTamanho() == "":
        print("Digite seu tamanho de roupa")
        tamanho = input()
        camisa.SetTamanho(tamanho)
        break

    print("Parabéns, você comprou uma roupa tamanho", camisa.getTamanho())

main()
